import React, {useState}  from "react"; 
import {SelectProgres} from "./../SelectProgres";

//Style Of Component
import './rowList.scss';

const RowList = ({ item, index ,listComplete, setListComplete }) => { 

	const [ showInput, setShowInput ]=useState( false) 

  const [ elementChainge, setElementChange ]= useState( item )

  const deleteElement = (index) => { 
		const newList = [...listComplete];
		newList.splice( index, 1) 
		setListComplete( newList )
	}

  const insertList = ( element, index ) => {
		const newList = [...listComplete];
		newList.splice( index, 1,element) 
		setListComplete( newList )
    setShowInput(-1)
  }

  const terzaFunzione = (index,) => {
    if(showInput){ 
      insertList( elementChainge, index)
    }
    setShowInput(!showInput)
  }


  return (
    <>
      {
        <div className="todolist-rowlist">
          {!showInput&&  <label className="view-table name">{ item.name } </label>}
          {showInput&& <input className="view-table name input" type="text" label={elementChainge.name} value={elementChainge.name}  onChange={ (event) => setElementChange({ ...elementChainge, name: event.target.value }) } />}
          {!showInput&&  <label className="view-table description"> { item.description } </label> }
          {showInput&&  <input className="view-table description input" type="text" label={elementChainge.description} value={elementChainge.description}  onChange={ (event) => setElementChange({ ...elementChainge, description: event.target.value }) } /> }
          <div className="view-table status input">
            <SelectProgres element={elementChainge}   setElement={setElementChange}/> 
          </div>
          <button className="view-table modification" onClick={ () => terzaFunzione(index)} > MODIFICATION </button>
          <button className="view-table delete" onClick={ () => deleteElement(index)} > DELETE </button>  
        </div>  
      }
    </>
  )
}

export default React.memo(RowList);
