import React , {useState}  from "react";

//Style Of Component
import './listGenerator.scss';


const ListGenerator = ({item,index}) => {

	const [ positionDescription, setPositionDescription ] = useState(-1)

	return( 
      <div className={"todolist-list-generator"} >
        <label className="view-table-name"> { item.name } </label>
        <label className="view-table-description"> { item.description } </label>
        { positionDescription!==index 
          ? <label className="view-table-description"> { item.description } </label>
          : <div>
              <input className="input name" type="text" label={item.description} value={item.description}  onChange={ (event) => setListComplete({ ...listComplete, description: event.target.value }) } /> 
              <button className="button-work modification" onClick={ () => setPositionDescription(-1)} /> 
            </div>
        }
        <label  className="view-table-status"> { item.status } </label>
        <button className="button-work delete" onClick={ () => deleteElement(index)} > </button> 
        <button className="button-work modification" onClick={ () => setPositionDescription(index)} > </button> 
      </div> 
  )
}

export default React.memo(ListGenerator);
