import React, {useState} from "react";

//Style Of Component
import "./modificationElement.scss"


const ModificationElement = ( { item, listComplete, setListComplete} ) => {


	const [ modWork, setModWork ]= useState({ id:item.id , name:item.name, description:item.description, status:item.status })



	return( 
      <div className={"modification-element"} >  
        <label className="view-table-name"> { item.name } </label>

        <input className="input name" type="text" label={modWork.description} value={modWork.description}  onChange={ (event) => setModWork({ ...listComplete, description: event.target.value }) } /> 

        <label  className="view-table-status"> { item.status } </label>
        <button className="button-work modification" > MODIFICATION </button>  
      </div> 
  )
}

export default React.memo(ModificationElement);
