import React, {useState} from "react";

//Style Of Component
import "./modificationElement.scss"

const ModificationElement = ( { item } ) => {

  const [ prova, setProva ]= useState( item )

  console.log(prova)

	return( 
      <div className={"modification-element"} >  
        <input className="input name" type="text" label={prova.name} value={prova.name}  onChange={ (event) => setProva({ ...prova, name: event.target.value }) } />
        <input className="input name" type="text" label={prova.description} value={prova.description}  onChange={ (event) => setProva({ ...prova, description: event.target.value }) } /> 
        <div className="custom-select" > 
          <select id="list-progress" value={ prova.status } onChange={ (event) => setProva({ ...prova, status: event.target.value }) }> 
            <option value="BACKLOG">backlog</option>
            <option value="TODO">to do</option>
            <option value="IN PROGRESS">in progress</option>
            <option value="COMPLETED">completed</option>
            <option value="IN REVIEW">in review</option>
            <option value="CLOSED">closed</option> 
          </select>
        </div>
      </div> 
  )
}

export default React.memo(ModificationElement);
