import React, {useState}  from "react";


const RowList = ({ item, index ,listComplete, setListComplete }) => { 

	const [modificationElemntView,setModificationElemntView]=useState(-1 ) 

  const [ elementChainge, setElementChainge ]= useState( item )

  const deleteElement = (index) => { 
		const newList = [...listComplete];
		newList.splice( index, 1) 
		setListComplete( newList )
	}

  const insertList = ( element, index ) => {
		const newList = [...listComplete];
		newList.splice( index, 1,element) 
		setListComplete( newList )
    setModificationElemntView(-1)
  }

  return (
    <>
      { 
        modificationElemntView!==index
          ? <div className="todolist-rowlist">
              <label className="view-table-name">{ item.name } </label>
              <label className="view-table-description"> { item.description } </label> 
              <label  className="view-table-status"> { item.status } </label>
              <button className="button-work delete" onClick={ () => deleteElement(index)} > DELETE </button> 
              <button className="button-work modification" onClick={ () => setModificationElemntView(index)} > MODIFICATION </button> 
            </div> 
          :<div className="modification-element" >  
            <input className="input name" type="text" label={elementChainge.name} value={elementChainge.name}  onChange={ (event) => setElementChainge({ ...elementChainge, name: event.target.value }) } />
            <input className="input name" type="text" label={elementChainge.description} value={elementChainge.description}  onChange={ (event) => setElementChainge({ ...elementChainge, description: event.target.value }) } /> 
            <div className="custom-select" > 
              <select id="list-progress" value={ elementChainge.status } onChange={ (event) => setElementChainge({ ...elementChainge, status: event.target.value }) }> 
                <option value="BACKLOG">backlog</option>
                <option value="TODO">to do</option>
                <option value="IN PROGRESS">in progress</option>
                <option value="IN REVIEW">in review</option>
                <option value="COMPLETED">completed</option>
                <option value="CLOSED">closed</option> 
              </select>
            </div>
            <button className="send-work-list"  onClick={ ()=> insertList( elementChainge, index) }>OK</button>
          </div> 
      }
    </>
  )
}

export default React.memo(RowList);
