import React from "react"; 

//Style Of Component
import './showDelete.scss';

const ShowDelete = ({listComplete, setListComplete} ) => {

  const deleteElement = (index) => { 
		const newList = [...listComplete];
		newList.splice( index, 1)
		setListComplete( newList )
	}



  return(
    <div className="todolist-ShowDelete">
    	{listComplete.map( (item,index) => {
						return(
							<div className={"view-table "+item.name} key={item.name+" "+index} >
								<label className="view-table-name"> { item.name } </label>
								<label className="view-table-description"> { item.description } </label>
								<label  className="view-table-status"> { item.status } </label>
								<button className="button-work delete" onClick={ () => deleteElement(index)} > </button> 
								<button className="button-work modification" onClick={ () => deleteElement(index)} > </button> 
							</div>
						)
					}
       )} 
    </div>
  )
}

export default React.memo(ShowDelete);
