import React,{useState} from "react"; 
import {ListGenerator} from "./../ListGenerator";
import {ModificationElement} from "./../ModificationElement";

//Style Of Component
import './showDelete.scss';

const ShowDelete = ({listComplete, setListComplete} ) => {

	const [ modification, setModification ] = useState(-1)

  const deleteElement = (index) => { 
		const newList = [...listComplete];
		newList.splice( index, 1)
		setListComplete( newList )
	}

  return(
    <div className="todolist-ShowDelete">
			<div className="view-table index">
				<label className="view-table-name index"> name </label>
				<label className="view-table-description index"> description </label>
				<label  className="view-table-status" > status </label>
				<label className="button-work delete index"> delete </label>
				<label  className="button-work modification index" index> modification </label>
			</div> 


    	{ 
				listComplete.map( (item,index) => {
					return(
						index!==modification
						? <ListGenerator item={item} index={index} deleteElement={deleteElement}  setModification={setModification}/>
						: <ModificationElement item={item}  listComplete={listComplete} setListComplete={setListComplete}  />
					)
				})
			} 


    </div>
  )
}

export default React.memo(ShowDelete);
