import React, {useState}  from "react";
import { Grid } from 'gridjs-react';


const RowList = ({ item, index ,listComplete, setListComplete }) => { 

	const [modificationElemntView,setModificationElemntView]=useState(-1 ) 

  const [ elementChainge, setElementChainge ]= useState( item )

  const columns = [ 'ASSIGNMENT', 'DESCRIPTION', 'STATUS', 'MODIFICATION', 'DELETE' ] 


  const deleteElement = (index) => { 
		const newList = [...listComplete];
		newList.splice( index, 1) 
		setListComplete( newList )
	}

  const insertList = ( element, index ) => {
		const newList = [...listComplete];
		newList.splice( index, 1,element) 
		setListComplete( newList )
    setModificationElemntView(-1)
  }

  return (
    <>

<Grid
  data={item}
  columns={columns}
  search={true}
  pagination={{
    enabled: true,
    limit: 1,
  }}
/>









      { 
        modificationElemntView!==index
          ? <div className="todolist-rowlist">
              <label className="view-table name"> {item.name} </label>
              <label className="view-table description"> { item.description } </label> 
              <label  className="view-table status"> { item.status } </label>
              <button className="view-table modification" onClick={ () => setModificationElemntView(index)} > MODIFICATION </button> 
              <button className="view-table delete" onClick={ () => deleteElement(index)} > DELETE </button> 
            </div> 
          :<div className="modification-element" >  
            <input className="view-table name-input" type="text" label={elementChainge.name} value={elementChainge.name}  onChange={ (event) => setElementChainge({ ...elementChainge, name: event.target.value }) } />
            <input className="view-table description-input" type="text" label={elementChainge.description} value={elementChainge.description}  onChange={ (event) => setElementChainge({ ...elementChainge, description: event.target.value }) } /> 
            <div className="view-table select-custom" > 
              <select id="view-table list-progress" value={ elementChainge.status } onChange={ (event) => setElementChainge({ ...elementChainge, status: event.target.value }) }> 
                <option value="BACKLOG">backlog</option>
                <option value="TODO">to do</option>
                <option value="IN PROGRESS">in progress</option>
                <option value="IN REVIEW">in review</option>
                <option value="COMPLETED">completed</option>
                <option value="CLOSED">closed</option> 
              </select>
            </div>
            <button className="view-table modification-ok-list"  onClick={ ()=> insertList( elementChainge, index) }>OK</button>
          </div> 
      }
    </>
  )
}

export default React.memo(RowList);
