import React from "react"; 

//Style Of Component
import './listGenerator.scss';

const ListGenerator = () => {

	const [ addWork, setAddWork ]= useState({ name:"", description:"", status:"" })

	const listComplete=[{
		name:"massimo",
		description:"creazone lista",
		status:"reiew", 
	}
]



	return(
		<div className="todolist-listgenerator">
			<div className="listgenerator-table-container">  
				<div  className="listgenerator-title" >
						<h1>TO DO LIST</h1>
				</div>

				<div className="creation-table" > 

					<div className="container name"> 
						<div className="title name"> 
							<label className="label name"> assegnazione </label>
						</div>  
						<div className="text name">  
							<input className="input name" type="text" label={addWork.name} value={addWork.name}  onChange={ (event) => setAddWork({ ...addWork, name: event.target.value }) } />
						</div> 
					</div>     

					<div className="container description"> 
						<div className="title description"> 
							<label className="label description"> descrizione </label>
						</div>  
						<div className="text description">  {
							//<input type="text" className="input description"  disabled={spec.readOnly} description={spec.description}/>
							}
						</div> 
					</div>  
					<div className="container status"> 
						<div className="title status"> 
							<label className="label status"> progressi </label>
						</div>  
						<div className="text status">  
							<input type="text" className="input status"/>
						</div> 
					</div>  
				</div>  

				{listComplete.map( (item,index) => {
						return(
							<div className="view-table" >
								<label className="view-table-name"> { item.name } </label>
								<label className="view-table-description"> { item.description } </label>
								<label  className="view-table-status"> { item.status } </label>
							</div>
						)
					}

					)} 

			</div> 
		</div>
	)
}

ListGenerator.propTypes = { 
}; 

ListGenerator.defaultProps = { 
};




export default React.memo(ListGenerator);
