import React, {useState}  from "react"; 

const RowList = ({ item, index ,listComplete, setListComplete }) => { 

	const [modificationElemntView,setModificationElemntView]=useState( false) 

  const [ elementChainge, setElementChainge ]= useState( item )

  const deleteElement = (index) => { 
		const newList = [...listComplete];
		newList.splice( index, 1) 
		setListComplete( newList )
	}

  const insertList = ( element, index ) => {
		const newList = [...listComplete];
		newList.splice( index, 1,element) 
		setListComplete( newList )
    setModificationElemntView(-1)
  }

  const terzaFunzione = (index,) => {
    if(modificationElemntView){ 
      insertList( elementChainge, index)
    }
    setModificationElemntView(!modificationElemntView)
  }

  return (
    <>
      {
        <div className="todolist-rowlist">
          {!modificationElemntView&&  <label className="view-table name">{ item.name } </label>}
          {modificationElemntView&& <input className="view-table name input" type="text" label={elementChainge.name} value={elementChainge.name}  onChange={ (event) => setElementChainge({ ...elementChainge, name: event.target.value }) } />}
          {!modificationElemntView&&  <label className="view-table description"> { item.description } </label> }
          {modificationElemntView&&  <input className="view-table description input" type="text" label={elementChainge.description} value={elementChainge.description}  onChange={ (event) => setElementChainge({ ...elementChainge, description: event.target.value }) } /> }
          {!modificationElemntView&&  <label  className="view-table status"> { item.status } </label>} 
          {modificationElemntView&& 
            <div className="view-table select input" > 
              <select className="view-table select-view input" id="view-table list-progress" value={ elementChainge.status } onChange={ (event) => setElementChainge({ ...elementChainge, status: event.target.value }) }> 
                <option value="BACKLOG">backlog</option>
                <option value="TODO">to do</option>
                <option value="IN PROGRESS">in progress</option>
                <option value="IN REVIEW">in review</option>
                <option value="COMPLETED">completed</option>
                <option value="CLOSED">closed</option> 
              </select>
            </div>  
            }
        <button className="view-table modification" onClick={ () => terzaFunzione(index)} > MODIFICATION </button>
        <button className="view-table delete" onClick={ () => deleteElement(index)} > DELETE </button>  
        </div>  
      }
    </>
  )
}

export default React.memo(RowList);
