import React, {useState} from "react"; 

//Style Of Component
import './showDelete.scss';

const ShowDelete = ({listComplete, setListComplete} ) => {

	const [ positionDescription, setPositionDescription ] = useState(-1)

  const deleteElement = (index) => { 
		const newList = [...listComplete];
		newList.splice( index, 1)
		setListComplete( newList )
	}

	const addWorkList = () => {  
		setAddWork({ ...addWork, id:listComplete.length  })    
		const newList = [...listComplete];
		newList.push(addWork)
		setListComplete( newList )
	} 


  return(
    <div className="todolist-ShowDelete">

							<div className="view-table index">
								<label className="view-table-name index"> name </label>
								<label className="view-table-description index"> description </label>
								<label  className="view-table-status" index> status </label>
								<label className="button-work delete index"> delete </label>
								<label  className="button-work modification index" index> modification </label>
							</div>

    	{listComplete.map( (item,index) => {
						return(
							<div className={"view-table "+item.name} key={item.name+" "+index} >
								<label className="view-table-name"> { item.name } </label>
								<label className="view-table-description"> { item.description } </label>
								{ positionDescription===index 
									? <label className="view-table-description"> { item.description } </label>
									: <input className="input name" type="text" label={item.description} value={item.description}  onChange={ (event) => addWorkList({ ...listComplete, description: event.target.value }) } /> 
								}
								<label  className="view-table-status"> { item.status } </label>
								<button className="button-work delete" onClick={ () => deleteElement(index)} > </button> 
								<button className="button-work modification" onClick={ () => setPositionDescription(index)} > </button> 
							</div>
						)
					}
       )} 
    </div>
  )
}

export default React.memo(ShowDelete);
