import React, {useState} from "react";
import {AddingElement} from "../../components/AddingElement";

const ToDoListView = ( ) => {

	const [listComplete,setListComplete ]=useState( [ ] )
	const [modificationElemnt,setModificationElemnt]=useState(-1 )
	const [ modWork, setModWork ]= useState( {} )

  const deleteElement = (index) => { 
		const newList = [...listComplete];
		newList.splice( index, 1) 
		setListComplete( newList )
	}


  return (
    <div className="todo-list-view">  
				<div  className="creationlist-title" >
						<h1>TO DO LIST</h1>
				</div>
				<AddingElement listComplete={listComplete} setListComplete={setListComplete} />  
        { 
          listComplete.map( (item,index) => {
            if(modificationElemnt===index){
              setModWork(item)
              return(
                <div className="todolist-list-generator">
                  <input className="input name" type="text" label={addWork.name} value={addWork.name}  onChange={ (event) => setAddWork({ ...addWork, name: event.target.value }) } />
                </div> 
              )
            }else
              return(
                <div className="todolist-list-generator">
                  <label className="view-table-name">snorlax </label>
                  <label className="view-table-description"> { item.description } </label> 
                  <label  className="view-table-status"> { item.status } </label>
                  <button className="button-work delete" onClick={ () => deleteElement(index)} > DELETE </button> 
                  <button className="button-work modification" onClick={ () => setModificationElemnt(index)} > MODIFICATION </button> 
                </div> 
              ) 
          })
			  } 
    </div>
  ); 
}
export default React.memo(ToDoListView);
